package com.bhavin.training;

public class SumSqure {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long sum = 0;
		for (int i=1; i<=30; i++) {
		sum = sum + i * i;
		System.out.println("The sum of squares of first 30 numbers is "  + sum );
		}
		System.out.println("The sum of squares of first 30 numbers is "  + sum );
	}

}
